@{
    var accordionClass = "accordion";
    if (Model.FlushStyle)
    {
        accordionClass += " accordion-flush";
    }
    var accordionId = Guid.NewGuid().ToString("N")[..8];
    ViewData["AccordionId"] = accordionId;
}

<div class="@accordionClass" id="@accordionId">
    @foreach (var item in Model.AccordionItems)
    {
        var content = item.Content;
        var itemId = (content.Key as Guid?)?.ToString("N")[..8] ?? "defaultId"; ;
        var collapseId = $"collapse{itemId}";
        var headingId = $"heading{itemId}";

        <div class="accordion-item">
            <h2 class="accordion-header" id="@headingId">
                <button class="accordion-button @(content.InitialStateShown ? "" : "collapsed")" 
                        type="button"
                        data-bs-toggle="collapse"
                        data-bs-target="#@collapseId"
                        aria-expanded="@(content.InitialStateShown ? "true" : "false")" 
                        aria-controls="@collapseId">
                    @content.Header
                </button>
            </h2>    <div id="@collapseId" 
                class="accordion-collapse collapse @(content.InitialStateShown ? "show" : "")" 
                aria-labelledby="@headingId" 
                data-bs-parent="#@accordionId">
                <div class="accordion-body">
                    @Html.Raw(content.Content)
                </div>
            </div>
        </div>
    }
</div>

