@{
    var tabsId = !string.IsNullOrWhiteSpace(Model.TabsId) ? Model.TabsId : Guid.NewGuid().ToString("N")[..8];
    var navClass = $"nav {Model.TabStyle}";

    if (!string.IsNullOrWhiteSpace(Model.Justification))
    {
        navClass += $" {Model.Justification}";
    }

    if (Model.FillSpace)
    {
        navClass += " nav-fill";
    }
    IEnumerable<Umbraco.Cms.Core.Models.Blocks.BlockListItem> blockListItems = Model.TabItems;
    var tabItems = blockListItems.Select((value, index) => new
    {
                    Content = value.Content,
                    Index = index
    }).ToList();
}
    <ul class="@navClass" id="@(tabsId)-tab" role="tablist">
        @foreach (var item in tabItems)
        {
            dynamic tabItem = item.Content;
            var tabId = !string.IsNullOrWhiteSpace(tabItem?.TabId) ? tabItem?.TabId : $"tab{item.Index}";
            var isActive = item.Index == 0;

            <li class="nav-item" role="presentation">
                <button class="nav-link @(isActive ? "active" : "")" 
                        id="@(tabId)-tab" 
                        data-bs-toggle="tab" 
                        data-bs-target="#@(tabId)-pane" 
                        type="button" 
                        role="tab" 
                        aria-controls="@(tabId)-pane" 
                        aria-selected="@(isActive ? "true" : "false")">
                    @tabItem?.Title
                </button>
            </li>
        }
    </ul>
    <div class="tab-content" id="@(tabsId)-tabContent">
        @foreach (var item in tabItems)
        {
            dynamic tabItem = item.Content;
            var tabId = !string.IsNullOrWhiteSpace(tabItem?.TabId) ? tabItem?.TabId : $"tab{item.Index}";
            var isActive = item.Index == 0;

            <div class="tab-pane fade @(isActive ? "show active" : "")" 
                 id="@(tabId)-pane" 
                 role="tabpanel" 
    using NPoco;
#line default
             tabindex="0">
                @Html.Raw(tabItem?.Content)
            </div>
        }
    </div>

@{
    var SizeMappings = (string? columnSize) => Model.Prefix + (columnSize switch
    {
        "12 Column (100%)" => "-12",
        "11 Column (92%)" => "-11",
        "10 Column (83%)" => "-10",
        "9 Column (75%)" => "-9",
        "8 Column (66%)" => "-8",
        "7 Column (58%)" => "-7",
        "6 Column (50%)" => "-6",
        "5 Column (42%)" => "-5",
        "4 Column (33%)" => "-4",
        "3 Column (25%)" => "-3",
        "2 Column (16%)" => "-2",
        "1 Column (8%)" => "-1",
        _ => ""
    });

    var GutterYMappings = (string? gutterY) => gutterY switch
    {
        "No Gutter (gy-0)" => "gy-0",
        "(gy-1)" => "gy-1",
        "(gy-2)" => "gy-2",
        "(gy-3)" => "gy-3",
        "(gy-4)" => "gy-4",
        "(gy-5)" => "gy-5",
        _ => ""
    };

    var GutterXMappings = (string? gutterX) => gutterX switch
    {
        "No Gutter (gx-0)" => "gx-0",
        "(gx-1)" => "gx-1",
        "(gx-2)" => "gx-2",
        "(gx-3)" => "gx-3",
        "(gx-4)" => "gx-4",
        "(gx-5)" => "gx-5",
        _ => ""
    };
}

<div class="row @Model.RowDirection @Model.RowCustomClass">
    @foreach (var column in Model.Columns)
    {
        IPublishedElement item = column.Content as IPublishedElement;
        <div class="@(SizeMappings(item.Value<string>("columnSize"))) @(GutterYMappings(item.Value<string>("GutterY"))) @(GutterXMappings(item.Value<string>("GutterX"))) @(item.Value<string>("CustomClass"))">
            <block-area identifier="column-@(item.Key.ToString("N").Substring(0, 8))"></block-area>
        </div>
    }
</div>
